module Tarefa1Spec (testesTarefa1) where

import LI12425
import Tarefa1
import Test.HUnit

testesTarefa1 :: Test
testesTarefa1 =
  TestLabel "Testes Tarefa 1" $
    test
      [ "validaInimigosPorLancar - inimigos válidos" ~: True ~=? validaInimigosPorLancar jogo,
        "validaInimigosSobreTerra - inimigos sobre terra" ~: True ~=? validaInimigosSobreTerra jogo,
        "validaInimigonaoSobrepostos - inimigos não sobrepostos" ~: True ~=? validaInimigonaoSobrepostos jogo,
        "validavelocidadeInimigo - velocidade válida" ~: True ~=? validavelocidadeInimigo jogo,
        "validaprojeteisNormalizados - projéteis normalizados" ~: True ~=? validaprojeteisNormalizados jogo,
        "validaBase - base válida" ~: True ~=? validaBase jogo,
        testNaPosicao,
        testValidaPortais,
        testValidaTorres,
        testValidaJogo
      ]

