testNaPosicao :: Test
testNaPosicao =
  TestList
    [ "Posicao (0.5,0.5) é Terra" ~: Terra ~=? naPosicao (0.5, 0.5) (mapaJogo jogo),
      "Posicao (2,4.2) é Terra" ~: Terra ~=? naPosicao (2, 5.2) (mapaJogo jogo),--
      "Posicao (4.2,0.6) é Agua" ~: Agua ~=? naPosicao (5.2, 0.6) (mapaJogo jogo)--
    ]

testValidaPortais :: Test
testValidaPortais =
  TestList
    [ "Portais sao validos em jogo default" ~: True ~=? validaPortais jogo,
      "Portais sao invalidos sem nenhum Portal" ~: False ~=? validaPortais (Jogo {lasersJogo = [], portaisJogo = [], baseJogo = baseJogo jogo, torresJogo = torresJogo jogo, mapaJogo = mapaJogo jogo, mapaDirecaoJogo = mapaDirecaoJogo jogo, inimigosJogo = inimigosJogo jogo, lojaJogo = lojaJogo jogo })
    ]

testValidaTorres :: Test
testValidaTorres =
  TestList
    [ "Torres sao validas em jogo default" ~: True ~=? validaTorres jogo,
      "Torres sao invalidas com posicoes sobrepostas"
        ~: False
        ~=? validaTorres
          ( Jogo
              { lasersJogo = [],
                torresJogo =
                  [ Torre {posicaoTorre = (0, 0), projetilTorre = Projetil Gelo (Finita 4.0), danoTorre = 50, alcanceTorre = 1.5, rajadaTorre = 3, cicloTorre = 2.0, tempoTorre = 0.0, defenidaTorre = True, menuAbertoTorre = False },
                    Torre {posicaoTorre = (0, 0), projetilTorre = Projetil Fogo (Finita 4.0), danoTorre = 50, alcanceTorre = 1.5, rajadaTorre = 3, cicloTorre = 2.0, tempoTorre = 0.0, defenidaTorre = True, menuAbertoTorre = False }
                  ],
                mapaJogo = mapaJogo jogo,
                mapaDirecaoJogo = mapaDirecaoJogo jogo,
                baseJogo = baseJogo jogo,
                portaisJogo = portaisJogo jogo,
                inimigosJogo = [],
                lojaJogo = []
              }
          )
        ]
                 
